var leftOffset = 0;
var moveOrb = function () {
    $('#purpleOrb').offset({left: leftOffset});

    leftOffset++;

    if (leftOffset > $(window).width()) {
        leftOffset = 0;
    }
};
setInterval(moveOrb, 30);


//var rt = 800;

var rt = $(window).width();

var moveElephant = function () {
    $("#elephant").offset({left: rt});

    rt--;

  if (rt > $(window).width() || rt === 0) {
        rt = $(window).width() - 100;
    }
};
setInterval(moveElephant, 30);